footer.script:
var elem = document.getElementById('isSvg');
var init = new Switchery(elem,{
    size: 'large'
});
function canvasBezier(a, b) {
    function c() {
        f.clearRect(0, 0, cwidth, cheight);
        for (var a = 3; a > 0; a--) {
            d[a - 1] += .5 * a;
            var c = (d[a - 1] + 120 * a) * Math.PI / 180;
            if (b) var e = Math.cos(c) * cheight * .09,
                g = Math.sin(c) * cheight * .06;
            else var e = Math.sin(c) * cheight * .06,
                g = Math.cos(c) * cheight * .09;
            f.setTransform(1, 0, 0, 1, 0, 0),
                f.strokeStyle = "rgba(" + bezierColor + (.2 * Math.cos(c) + .5) + ")",
                f.beginPath(),
                f.moveTo(0, .5 * cheight + e),
                f.bezierCurveTo(cwidth / 2, .5 * cheight + 2.5 * e, cwidth / 2, .5 * cheight + 2 * g, cwidth, .5 * cheight + g),
                f.stroke()
        }
    }
    var d = [0, 0, 0],
        e = document.getElementById(a),
        f = e.getContext("2d");
    cwidth = document.body.clientWidth >= 1130 ? document.body.clientWidth : 1130,
        cheight = document.body.clientHeight,
        e.width = cwidth,
        e.height = cheight,
        f.lineWidth = .5,
        e.timer = setInterval(c, 30),
        window.addEventListener("resize", function(){
            clearInterval(e.timer),
                cwidth = document.body.clientWidth >= 1130 ? document.body.clientWidth : 1130,
                cheight = document.body.clientHeight,
                e.width = cwidth,
                e.height = cheight,
                f.lineWidth = 1,
                e.timer = setInterval(c, 30)
        }, true)
}
var bezierColor = "150,150,150,"
canvasBezier("bgcanvas", 0);
document.getElementById('portal_search').addEventListener('submit',function () {
    var value=document.getElementById('search_input').value.toLowerCase();
    value=value.replace('icon sets','').replace('icons','').replace('icon','').replace(/[^\w\s]|_/g, " ").replace(/\s+/g, " ");
    var value_arr=value.split(' ').filter(function(e){return e});
    value=value_arr.join('-');
    if(document.getElementById('isSvg').checked){
        location.href='https://www.iconninja.com/tag/'+value+'-vector-icon';
    }else{
        location.href='https://www.iconninja.com/tag/'+value+'-icon';
    }
});
